import React, {Component} from 'react';

import {Card, Form, Button} from 'react-bootstrap';

import axios from 'axios';

export default class Welcome extends Component {

    constructor(props) {
        super(props);
        this.state = this.initialState;
        this.loginChange = this.loginChange.bind(this);
        this.submitLogin = this.submitLogin.bind(this);
    }

    initialState = {
        name:'root', password:'root'
    }

    submitLogin = event => {
        event.preventDefault();

        const login = {
            name: this.state.name,
            password: this.state.password
        };

        axios({
            method:'post',
            url:'/login',
            params: {
                name: this.state.name,
                password: this.state.password
            },
            config: { headers: {'Content-Type': 'application/x-www-form-urlencoded'}}
            })
            .then(response => {
                if(response.data != null) {
                    this.setState(this.initialState);
                    alert("Login success!");
                }
            })
            .catch(error=>{
                var errResp = error.response;
                if(errResp.status === 401){
                    alert(error.response);
                    
                }

            });
    }

    loginChange = event => {
        this.setState({
            [event.target.name]:event.target.value
        });
    }

    resetLogin = () => {
        this.setState(() => this.initialState);
    }

    render() {
        const {name, password} = this.state;

        return (
            <Card className={"border border-dark bg-dark text-white"} style={{ width: '30rem' }}>
                <Card.Header>Login</Card.Header>
                <Form onSubmit={this.submitLogin} onReset={this.resetLogin} id="loginFormId">
                    <Card.Body>
                        <Form.Group controlId="formLoginName">
                            <Form.Label>Username</Form.Label>
                            <Form.Control autoComplete="off"
                                          name="name"
                                          type="test"
                                          value={name}
                                          onChange={this.loginChange}
                                          className={"bg-dark text-white"}
                                          placeholder="Enter username"
                            />
                        </Form.Group>

                        <Form.Group controlId="formLoginName">
                            <Form.Label>Password</Form.Label>
                            <Form.Control autoComplete="off"
                                          name="password"
                                          type="test"
                                          value={password}
                                          onChange={this.loginChange}
                                          className={"bg-dark text-white"}
                                          placeholder="Enter password"
                            />
                        </Form.Group>
                    </Card.Body>

                    <Card.Footer style={{"textAlign":"right"}}>
                        <Button variant="success" type="submit">Submit</Button>{' '}
                        <Button variant="info" type="reset">Reset</Button>
                    </Card.Footer>
                </Form>
            </Card>
        );
    }
}
